import { Component, OnInit } from '@angular/core';
import { AnimalsService } from '../services/animals.service';
import { Animal } from '../models/animal';
import { Router } from '@angular/router';
import { Proposal } from '../models/proposal';
import { ProposalsService } from '../services/proposals.service';
import { User } from '../models/user';

@Component({
  selector: 'app-visitor-start',
  templateUrl: './visitor-start.component.html',
  styleUrls: ['./visitor-start.component.css']
})
export class VisitorStartComponent implements OnInit{

  constructor(private animalService: AnimalsService,private router: Router, private proposalService: ProposalsService){}

  ngOnInit(): void {
    this.getAnimals();
    this.user = JSON.parse(localStorage.getItem("loggedUser"));
    this.getProposals();
    this.votes = this.getVotes()
  }

  animals: Animal[] = [];

  getAnimals(){
    this.animalService.getAnimals().subscribe(
      data=>{
        this.animals = data;
      }
    );
  }

  sortAnimals(){
    this.animals = this.animalService.sortByWeight(this.animals);
  }

  logout(){
    localStorage.setItem("loggedUser", null);
    this.router.navigate([""]);
  }

  proposal: Proposal = new Proposal();
  user: User = new User();

  addProposal(){
    this.proposal.visitor = this.user.username;
    this.proposalService.addProposal(this.proposal).subscribe(
      data=>{
        if(data==1){
          alert("Uspesno dodavanje!");
        }
      }
    )
  }

  proposals: Proposal[] = [];
  votes: number[] = [];

  getProposals(){
    this.proposalService.getProposals().subscribe(
      data=>{
        this.proposals = data;
      }
    )
  }

  getVotes(){
    this.proposalService.getVotes(this.user).subscribe(
      data=>{
        this.votes = data;
      }
    )
  }

  vote(idp: number){
    alert(this.proposals)
    this.proposalService.vote(idp, this.user.username).subscribe(
      data=>{
        for(let i = 0;i< this.proposals.length;i++){
          console.log(this.proposals[i])
        }
      }

    )
  }

}
<h2>Dobrodosli posetioci</h2>
<hr>
<h3>Pregled zivotinja</h3>
<div>
    <button (click)="sortAnimals()">Sortiraj</button>
    <table>
        <tr>
            <th>Zivotinja</th>
            <th>Tezina</th>
        </tr>
        <tr *ngFor="let animal of animals">
            <td>{{animal.name}}</td>
            <td>{{animal.weight}}</td>
        </tr>
    </table>
</div>
<hr>
<h3>Glasanje za predloge</h3>
<table>
    <tr>
        <th>Posetilac</th>
        <th>Zivotinja</th>
        <th>Komentar</th>
        <th>Glasovi</th>
        <th>Akcija</th>
    </tr>
    <tr *ngFor="let p of proposals">
        <td>{{p.visitor}}</td>
        <td>{{p.animal}}</td>
        <td>{{p.comment}}</td>
        <td>{{p.votes}}</td>
        <td>
            <div *ngIf="!p.approved">
                <div *ngIf="!votes.includes(p.idP)">
                    <button (click)="vote(p.idP)">Glasaj</button>
                </div>  
            </div>
        </td>
    </tr>
</table>
<hr>
<h3>Davanje predloga</h3>
Naziv zivotinje:
<input type="text" [(ngModel)]="proposal.animal">
<br>
Opis predloga:
<input type="text" [(ngModel)]="proposal.comment">
<br>
<button (click)="addProposal()">Dodaj predlog</button>
<hr>
<button (click)="logout()">Odjavi se</button>
