import { Component, OnInit } from '@angular/core';
import { Proposal } from '../models/proposal';
import { AnimalsService } from '../services/animals.service';
import { Router } from '@angular/router';
import { ProposalsService } from '../services/proposals.service';

@Component({
  selector: 'app-worker-start',
  templateUrl: './worker-start.component.html',
  styleUrls: ['./worker-start.component.css']
})
export class WorkerStartComponent implements OnInit{

  constructor(private animalService: AnimalsService,private router: Router, private proposalService: ProposalsService){}


  ngOnInit(): void {
    this.getProposals();
  }

  proposals: Proposal[] = [];

  getProposals(){
    this.proposalService.getProposals().subscribe(
      data=>{
        this.proposals = data;
      }
    )
  }

  approve(p:Proposal){

  }

}
<h2>Hello worker!</h2>
<h3>Glasanje za predloge</h3>
<table>
    <tr>
        <th>Posetilac</th>
        <th>Zivotinja</th>
        <th>Komentar</th>
        <th>Glasovi</th>
        <th>Odobravanje</th>
        <th>Odbijanje</th>
    </tr>
    <tr *ngFor="let p of proposals" [class.zel]="p.votes>2">
        <td>{{p.visitor}}</td>
        <td>{{p.animal}}</td>
        <td>{{p.comment}}</td>
        <td>{{p.votes}}</td>
        <td>
            <div *ngIf="!p.approved">
                <button (click)="approve(p)">Odobri</button>
            </div>
        <td>
            <div *ngIf="!p.approved">
                <button (click)="decline(p)">Odbij</button>
            </div>
        </td>
    </tr>
</table>
