import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ItemService } from '../services/item.service';
import { Item } from '../models/item';

@Component({
  selector: 'app-customer-start',
  templateUrl: './customer-start.component.html',
  styleUrls: ['./customer-start.component.css']
})
export class CustomerStartComponent implements OnInit{

  constructor(private router: Router, private itemService: ItemService){}

  ngOnInit(): void {
    this.getItems()
  }

  errorMsg2: string;
  items: Item[];

  getItems(){
    this.itemService.getItems().subscribe(
      data=>{
        if(!data){
          this.errorMsg2="Nema najboljeg kupca!"
          return;
        }
        this.items=data;
      }
    );
  }

  logout(){
    this.router.navigate([""]);
  }

}
<h2>Dobrodosli na pocetku stranu kupaca!</h2>
<hr>
<table>
    <tr>
        <th>Proizvod</th>
        <th>Opis</th>
        <th>Cena</th>
        <th>Izaberi</th>
        <th>Kolicina</th>
    </tr>
    <tr *ngFor="let item of items" [class.zel]="item.promotion==1">
        <td>{{item.name}}</td>
        <td>{{item.description}}</td>
        <td>{{item.price}}</td>
        <td><input type="checkbox"></td>
        <td><input type="number"></td>
    </tr>
    <tr>
        <button>Naruci</button>
    </tr>
</table>
<hr>
<button (click)="logout()">Odjavi se</button>
