import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { User } from '../models/user';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-component',
  templateUrl: './login-component.component.html',
  styleUrls: ['./login-component.component.css']
})
export class LoginComponentComponent implements OnInit{

  constructor(private userService: UserService, private router: Router){}

  ngOnInit(): void {
  }

  username: string;
  password: string;
  type: string;
  errorMsg1: string;

  user: User = new User();

  login(){
    if(!this.username || !this.password || !this.type){
      this.errorMsg1 = "Niste 1 uneli sve potrebne podatke!";
      return;
    }
    this.user.username = this.username;
    this.user.password = this.password;
    this.user.type = this.type;
    this.userService.login(this.user).subscribe(
      data=>{
        if(!data){
          this.errorMsg1 = "Nepostojeci korisnik!";
          return;
        }
        if(data.type == "kupac"){
          this.router.navigate("customer-start")
        }else{

        }
      }
    )
  }

}
<h2>Dobrodosli na sajt PET 2021!</h2>
<form name="loginForm">
    <table>
        <tr>
            <td>Korisnicko ime: </td>
            <td><input name="username" type="text" [(ngModel)]="username"></td>
        </tr>
        <tr>
            <td>Lozinka: </td>
            <td><input name="password" type="password" [(ngModel)]="password"></td>
        </tr>
        <tr>
            <td>Tip: </td>
            <td>
                <select name="select" id="" [(ngModel)]="type">
                    <option value="kupac">Kupac</option>
                    <option value="prodavac">Prodavac</option>
                </select>
            </td>
        </tr>
        <tr>
            <button (click)="login()">Uloguj se</button>
        </tr>
    </table>
    <span style="color: red;">
        {{errorMsg1}}
    </span>
</form>
