import { Component, OnInit } from '@angular/core';
import { ItemService } from '../services/item.service';
import { OrderService } from '../services/order.service';
import { Router } from '@angular/router';
import { Item } from '../models/item';
import { User } from '../models/user';
import { Order } from '../models/order';

@Component({
  selector: 'app-worker',
  templateUrl: './worker.component.html',
  styleUrls: ['./worker.component.css']
})
export class WorkerComponent implements OnInit{
  constructor(private itemService:ItemService,private orderService:OrderService,private router:Router){}
  ngOnInit(): void {
    this.getItems()
    this.getOrders()
    let x = localStorage.getItem("user")
    if(x){
      this.user=JSON.parse(x)
    }
  }

  items: Item[] = []
  user:User=new User()
  searchParam:string=""
  orders:Order[]=[]

  getItems(){
    this.itemService.getItems().subscribe(
      data=>{
        if(data){
          this.items=data;
        }
      }
    )
  }

  logout(){
    this.router.navigate([""])
  }

  search(){
    this.items = this.items.filter(item=> item.name.toLowerCase().includes(this.searchParam.toLowerCase()))
  }

  getOrders(){
    this.orderService.getOrders().subscribe(
      data=>{
        if(data){
          this.orders=data
        }
      }
    )
  }
}
<h3>Prodavac</h3>
<hr>
<h4>Proizvodi</h4>
<table>
    <tr>
        <th>Naziv</th>
        <th>Cena</th>
    </tr>
    <tr *ngFor="let i of items">
        <td>{{i.name}}</td>
        <td>{{i.price}}</td>
    </tr>
</table>
<input type="text" [(ngModel)]="searchParam"> |
<button (click)="search()">Pretrazi</button>
<hr>
<table>
    <tr>
        <th>Narudzbina</th>
        <th>Proizvod</th>
        <th>Kolicina proizvoda</th>
        <th>Kupac</th>
    </tr>
    <tr *ngFor="let o of orders">
        <td>{{o.idN}}</td>
        <td>{{o.itemName}}</td>
        <td>{{o.quantity}}</td>
        <td>{{o.customer}}</td>
    </tr>
</table>
<hr>
<button (click)="logout()">Odjavi se</button>
