import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { User } from '../models/user';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent{

  constructor(private userService: UserService, private router: Router){

  }

  username: string;
  password:string;
  type:string;
  userLogin: User = new User();
  errorMsg1: string;  

  login(){
    if(!this.username || !this.password || !this.type){
      this.errorMsg1 = "Niste uneli sve podatke!";
      return;
    }
    this.userService.login(this.userLogin).subscribe(
      data=>{
        if(!data){
          this.errorMsg1 = "Uneli ste nevalidne podatke!";
          return;
        }
        localStorage.setItem("loggedUser", JSON.stringify(data));
        if(data.type=="radnik"){
          this.router.navigate(["worker-start"]);
        }else{
          this.router.navigate(["visitor-start"]);
        }
      }
    );
  }

}
<h2>Dobrodosli na pocetnu stranu Zoo2020!</h2>
<div name="login">
    <table>
        <tr>
            <td>Korisnicko ime:</td>
            <td><input type="text" [(ngModel)]="username"></td>
        </tr>
        <tr>
            <td>Lozinka:</td>
            <td><input type="password" [(ngModel)]="password"></td>
        </tr>
        <tr>
            <td>Tip:</td>
            <td><input type="radio" name="tip" [(ngModel)]="type" value="radnik">Radnik</td>
            <td><input type="radio" name="tip" [(ngModel)]="type" value="posetilac">Posetilac</td>
        </tr>
        <tr>
            <button (click)="login()">Uloguj se</button>
        </tr>
    </table>
</div>
<span>{{errorMsg1}}</span>
