import { Component, OnInit } from '@angular/core';
import { AnimalsService } from '../services/animals.service';
import { Animal } from '../models/animal';

@Component({
  selector: 'app-visitor-start',
  templateUrl: './visitor-start.component.html',
  styleUrls: ['./visitor-start.component.css']
})
export class VisitorStartComponent implements OnInit{

  constructor(private animalService: AnimalsService){}

  ngOnInit(): void {
    this.getAnimals();
  }

  animals: Animal[] = [];

  getAnimals(){
    this.animalService.getAnimals().subscribe(
      data=>{
        this.animals = data;
      }
    );
  }

  sortAnimals(){
    this.animals = this.animalService.sortByWeight(this.animals);
  }

}
<h2>Dobrodosli posetioci</h2>
<div>
    <button>Sortiraj</button>
    <table>
        <tr>
            <th>Zivotinja</th>
            <th>Tezina</th>
        </tr>
        <tr *ngFor="let animal of animals">
            <td>{{animal.name}}</td>
            <td>{{animal.weight}}</td>
        </tr>
    </table>

</div>
