import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CommentService } from '../services/comment.service';
import { Item } from '../models/Item';
import { ItemService } from '../services/item.service';

@Component({
  selector: 'app-customer-start',
  templateUrl: './customer-start.component.html',
  styleUrls: ['./customer-start.component.css']
})
export class CustomerStartComponent {

  constructor(private commentService: CommentService, private router: Router, private itemService: ItemService){}

  logout(){
    this.router.navigate([""]);
  }

  items: Item[] = []

  getItems(){
    this.itemService.getItems().subscribe(
      data=>{
        if(!data){
          alert("Nema itema")
        }
        this.items=data;
      }
    )
  }
}
<hr>
<table>
    <tr>
        <th>Naziv</th>
        <th>Opis</th>
        <th>Cena</th>
    </tr>
    <tr *ngFor="let item of items">
        <td>{{item.name}}</td>
        <td>{{item.description}}</td>
        <td>{{item.price}}</td>
    </tr>
</table>
<hr>
<button (click)="logout()">Odjavi se</button>
