import { Component,OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CommentService } from '../services/comment.service';
import { User } from '../models/User';
import { Comment } from '../models/Comment';

@Component({
  selector: 'app-worker',
  templateUrl: './worker.component.html',
  styleUrls: ['./worker.component.css']
})
export class WorkerComponent implements OnInit{
  constructor(private commentService: CommentService, private router: Router){}
  ngOnInit(): void {
    let y = localStorage.getItem("user")
    if(y){
      this.user=JSON.parse(y)
    }
    this.getAllComments()
  }
  user: User = new User()

  logout(){
    this.router.navigate([""]);
  }

  comments: Comment[]=[]
  x:string="Neobradjeno"

  getAllComments(){
    this.commentService.getAll().subscribe(
      data=>{
        if(data){
          this.comments=data
        }
      }
    )
  }

  accept(c:Comment){
    this.commentService.accept(c).subscribe(
      data=>{
        if(data==1){
          alert("Uspesno")
        }
      }
    )
  }

  decline(c:Comment){
    this.commentService.decline(c).subscribe(
      data=>{
        if(data==1){
          alert("Uspesno")
        }
      }
    )
  }
}
<h2>Hello worker</h2>
<hr>
<table>
    <tr>
        <th>Ime</th>
        <th>Prezime</th>
        <th>Rodjendan</th>
    </tr>
    <tr>
        <td>{{user.name}}</td>
        <td>{{user.surname}}</td>
        <td>{{user.birthday}}</td>
    </tr>
</table>
<hr>
<h3>Komentari</h3>
<table>
    <tr>
        <th>Ime kupca</th>
        <th>Prezime kupca</th>
        <th>Proizvod</th>
        <th>Tekst</th>
        <th>Prihvati</th>
        <th>Obrisi</th>
    </tr>
    <tr *ngFor="let c of comments" [class.oran]="c.state=='Neobradjeno'">
        <td></td>
        <td></td>
        <td>{{c.item}}</td>
        <td>{{c.text}}</td>
        <td>
            <div *ngIf="c.state=='Neobradjeno'">
                <button (click)="accept(c)">Prihvati</button>
            </div>
        </td>
        <td>
            <div *ngIf="c.state=='Neobradjeno'">
                <button (click)="decline(c)">Obrisi</button>
            </div>
        </td>
    </tr>
</table>
<hr>
<button (click)="logout()">Odjavi se</button>
